mapper.insert:
INSERT INTO t_user
SET uid = #{uid}
, upw = #{upw}
, nm = #{nm}
, pic = #{pic}

mapper.select:
SELECT iuser, uid, upw, nm, pic, created_at AS createdAt, updated_at AS updatedAt
FROM t_user


        uid = #{uid}


        iuser = #{iuser}

mapper.insert:
INSERT INTO t_user_follow
SET from_iuser = #{fromIuser}
, to_iuser = #{toIuser}

mapper.delete:
DELETE FROM t_user_follow
WHERE from_iuser = #{fromIuser}
AND to_iuser = #{toIuser}
